/* eslint-disable jsx-a11y/img-redundant-alt */
import React, {Component} from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Registration from "./Views/Registration";
import Login from "./Views/Login";
import Dashboard from "./Views/Dashboard";
import axios from "axios";
import "./App.css";
import Home from "./Views/Home";

export default class App extends Component {
  constructor() {
    super();

    this.state = {
      loggedIn: "NOT_LOGGED_IN",
      user: {}
    }

    this.handleLogin = this.handleLogin.bind(this);
  }
  checkLoginStatus() {
    axios.get("https://626b502d6a86cd64adba3e18.mockapi.io/user", { headers: {
      'Content-Type': 'application/json;charset=UTF-8',
      "Access-Control-Allow-Origin": "*",
      withCredentials: true
  }}, {withCredentials: true}).then(response =>
    console.log(response))}

    componendDidMount() {
      this.checkLoginStatus()
    }

  handleLogin(data) {
    this.setState({
      loggedIn: "LOGGED_IN" ,
      user: data.user
    })
  }


  render() {
    return (
      <Router>
        <div>
          <Routes>
            <Route exact path="/" element={<Home handleLogin={this.handleLogin} loggedIn={this.state.loggedIn} />}/>
            <Route exact path="/login" element={<Login  />}/>  
            <Route exact path="/register" element={<Registration    />}/>
            <Route exact path="/dashboard" element={<Dashboard handleLogin={this.handleLogin}  loggedIn={this.state.loggedIn} />}/>
          </Routes>
        </div>
      </Router>
    );
  }
}
